import React from 'react';
import ReactDOM from 'react-dom/client';
import './MusicPlayer.css';

import DefaultAlbumArt from './Images/DefaultAlbumArt.png';  //'../Images/albumCover.webp';
import Play from './Images/play.svg';
import Pause from './Images/pause.svg';
import SkipNext from './Images/skip-next.svg';
import SkipPrev from './Images/skip-prev.svg';

function MusicPlayer({ PlayOrPauseFunc }) {
    function PlayPauseHandle() {

    }
    return (
        <div className='MusicPlayer'>
            <div className='SongImage'>
                <img src={DefaultAlbumArt}></img>
            </div>
            <div className='SongDetails'>

                <h2>Nothing Playing</h2>
                <p>-</p>
            </div>
            <div className='Btns'>
                <button className='Prev'><img src={SkipPrev} /></button>
                <button className='Play'><img src={Play} onClick={() => PlayOrPauseFunc()} /></button>
                <button className='Next'><img src={SkipNext} /></button>
            </div>
        </div>
    );
}



export default MusicPlayer;
